div.script:
function showCommentsFor(page) {
   console.log('showCommentsFor(' + page + ')');
   init(page, $('#cmts'));

   return false;
}

var pageLocation = window.location.href;
try {
    var p = parseUri(pageLocation);
    console.log(p);
    showCommentsFor(p.path);
} catch (e) {
    debug(e);
}
